import React from 'react';
import { MDBCard, MDBCardBody, MDBCardTitle, MDBCardSubTitle, MDBCardText, MDBCardImage, MDBBtn, MDBRipple, MDBRow, MDBCol } from 'mdb-react-ui-kit';
import { BASE_URL } from '../../constants/constants';
import { useNavigate } from 'react-router-dom';
import './cards.css'; // Import your custom CSS file

export default function Cards({court}) {
  const navigate = useNavigate();

  return (
    <MDBCard className='card d' onClick={() => navigate(`/courtUserViewPage/${court._id}`)}>
      <MDBRipple rippleColor='light' rippleTag='div' className='card-image image text-center pt-3'>
        <MDBCardImage src={`${BASE_URL}/courts/${court.courtPic}`} fluid alt='...' />
      </MDBRipple>
      <MDBCardBody className='card-body'>
        {/* Using MDBRow and MDBCol to organize card details */}
        <MDBRow>
          <MDBCol size="12">
            <MDBCardTitle className='card-title title'>Name: {court?.courtName}</MDBCardTitle>
          </MDBCol>
          <MDBCol size="6">
            <MDBCardSubTitle className='card-subtitle subtitle'>Type: {court?.type}</MDBCardSubTitle>
          </MDBCol>
          <MDBCol size="6">
            <MDBCardSubTitle className='card-subtitle subtitle'>Location: {court?.location}</MDBCardSubTitle>
          </MDBCol>
          <MDBCol size="12">
            <MDBCardText className='card-text text'>About: {court.about}</MDBCardText>
          </MDBCol>
        </MDBRow>
        <MDBBtn  className="btn text-light ms-2" style={{background: '  #010203'}}>Button</MDBBtn>
      </MDBCardBody>
    </MDBCard>
  );
}
