import React, { useEffect, useState } from 'react';
import Axiosinstance from '../../config/Axiosinstances';
import { useParams } from 'react-router-dom';
import NavScrollExample from './Navbar';
import Card from 'react-bootstrap/Card';
import { BASE_URL } from '../../constants/constants';

function GroupDetailPage() {
    const { groupId } = useParams();
    const [group, setGroup] = useState(null);

    useEffect(() => {
        fetchGroupDetails();
    }, [groupId]); // Include groupId in the dependency array

    const fetchGroupDetails = () => {
        Axiosinstance.get(`/users/groups/${groupId}`)
            .then(response => {
                setGroup(response.data);
                console.log(response.d)
            })
            .catch(error => {
                console.error('Error fetching group details:', error);
            });
    };

    if (!group) {
        return <div>Loading...</div>;
    }

    return (
        <div>
            <NavScrollExample />
            <h1>Group Details</h1>
            <h2>Group Name: {group.groupName}</h2>
            <div>
                {group.courts.map((court, index) => (
                    <Card key={index} style={{ width: '18rem', margin: '10px' }}>
                        <Card.Img variant="top" src={`${BASE_URL}/courts/${court.image}`} alt={court.courtName} />
                        <Card.Body>
                            <Card.Title>{court.courtName}</Card.Title>
                            <Card.Subtitle className="mb-2 text-muted">Location: {court.location}</Card.Subtitle>
                            <Card.Text>
                                {court.about}
                            </Card.Text>
                            {/* Add any other details you want to display */}
                            <Card.Link href="#">View Court Details</Card.Link>
                        </Card.Body>
                    </Card>
                ))}
            </div>
        </div>
    );
}

export default GroupDetailPage;
