import React, { useState } from 'react';
import { Link } from 'react-router-dom';

function ButtonBooking() {
  const [players, setPlayers] = useState([]);

  const handleButtonClick = (playerData) => {
    setPlayers([...players, playerData]);
  };

  return (
    <div>
      <h1>Court Booking Page</h1>
      <button onClick={() => handleButtonClick({ name: 'Player 1', image: 'image1.jpg', price: '$10' })}>Button 1</button>
      <button onClick={() => handleButtonClick({ name: 'Player 2', image: 'image2.jpg', price: '$20' })}>Button 2</button>
      <button onClick={() => handleButtonClick({ name: 'Player 3', image: 'image3.jpg', price: '$30' })}>Button 3</button>
      <button onClick={() => handleButtonClick({ name: 'Player 4', image: 'image4.jpg', price: '$40' })}>Button 4</button>

      <Link to={{ pathname: '/my-player', state: { players } }}>MyPlayers</Link>
    </div>
  );
}

export default ButtonBooking;
