import React, { useEffect, useState } from 'react';
import Axiosinstance from '../../config/Axiosinstances';
import NavScrollExample from './Navbar';

function MyGroupsPage() {
    const [groups, setGroups] = useState([]);

    useEffect(() => {
        fetchGroupsAndCourts();
    }, []);

    const fetchGroupsAndCourts = () => {
        Axiosinstance.get('/users/getAllGroups')
            .then(response => {
                setGroups(response.data);
            })
            .catch(error => {
                console.error('Error fetching groups:', error);
            });
    };

    return (
        <div>
            <NavScrollExample />
            <h1>All Groups with Courts</h1>
            {groups.length > 0 ? (
                groups.map(group => (
                    <div key={group._id}>
                        <h2>{group.groupName}</h2>
                        <ul>
                            {group.courts.map(court => (
                                <li key={court._id}>
                                    <h3>{court.courtName}</h3>
                                    <p>Location: {court.location}</p>
                                    <p>About: {court.about}</p>
                                    <img src={court.image} alt={court.courtName} />
                                    {/* Add any other court details you want to display */}
                                </li>
                            ))}
                        </ul>
                    </div>
                ))
            ) : (
                <p>No groups found</p>
            )}
        </div>
    );
}

export default MyGroupsPage;
