import React, { useEffect, useState } from 'react';
import NavScrollExample from '../components/common/Navbar';
import { Carousel } from 'react-bootstrap';
import Axiosinstance from '../config/Axiosinstances';
import { useNavigate } from 'react-router-dom';
import GroupCard from '../components/common/'; // Make sure to import GroupCard
import '.././pages/css/Home.css'; // Import your custom CSS file
import c1 from './pictures/L1.jpg';
import c2 from './pictures/L2.jpg';
import c4 from './pictures/L4.jpeg';
import c5 from './pictures/L5.jpeg';

function Teamsview() {
    const [teamData, setTeamData] = useState([]);
    const [groupData, setGroupData] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        fetchData();
    }, []);

    const fetchData = () => {
        getAllGroupsData();
        getAllTeamsData();
    };

    const getAllTeamsData = () => {
        Axiosinstance.get('/users/getAllTeamsData')
            .then((response) => {
                setTeamData(response.data);
            })
            .catch((err) => {
                console.error('Error fetching teams:', err);
            });
    };

    const getAllGroupsData = () => {
        Axiosinstance.get('/users/getGroups')
            .then((response) => {
                setGroupData(response.data);
            })
            .catch((err) => {
                console.error('Error fetching groups:', err);
            });
    };

    const filterTeamsByGroup = () => {
        if (!teamData || !teamData.length) return [];

        const matchedTeamIds = groupData.reduce((acc, group) => {
            const teamIdsInGroup = group.teams.map(team => team.teamName);
            return [...acc, ...teamIdsInGroup];
        }, []);

        const filteredTeams = teamData.filter(team => !matchedTeamIds.includes(team.teamName));
        return filteredTeams;
    };

    return (
        <>
            <NavScrollExample />
            <div id="carouselExampleControls" className="carousel slide text-center" data-bs-ride="carousel">
                {/* Carousel content */}
            </div>
            <div className="d-flex ">
                {/* Marquee content */}
            </div>
            <div className="container-fluid f pb-3">
                <u><h1 className='text-center fw-bold pr'>TEAMS</h1></u>
            </div>
            <div className="container-fluid mt-2 L">
                <div className="row row-cols-1 row-cols-md-5 mx-2 g-4">
                    {/* Render teams */}
                    {filterTeamsByGroup().map((team) => (
                        <div key={team._id} className="col">
                            <GroupCard team={team} />
                        </div>
                    ))}
                </div>
            </div>
        </>
    );
}

export default Teamsview;
