import React, { useEffect, useState } from 'react'
import NavScrollExample from '../components/common/Navbar'
import { Carousel } from 'react-bootstrap';
import Axiosinstance from '../config/Axiosinstances';
import Cards from '../components/common/Cards';
import { useNavigate } from 'react-router-dom';
import './css/Home.css'; // Import the custom CSS file
import { BASE_URL } from '../constants/constants';


function Teamsview() {
const [courtData, setCourtData] = useState([])
const navigate= useNavigate()
useEffect(() => {
 getAllteamsData()
}, []);
const getAllteamsData=()=>{
  Axiosinstance.get('/users/getAllteamsData').then((response)=>{
    setCourtData(response.data)
    console.log(response.data);

  })
  .catch(err=>{
    if (err.response.data.message==='unauthorized user')
    { localStorage.clear()
       navigate('/')
 }
  })
}





  return (
  <>
  <NavScrollExample />
      <div className="container-fluid p-b ">
        <Carousel>
          {courtData.map((team) => (
            <Carousel.Item key={court.id}>
              <img
                className="d-block w-100"
                src={`${BASE_URL}/courts/${team.teamPic}`} // Change this with your actual image URL
                alt={team.teamName}
              />
              <Carousel.Caption>
                <h3>{team.teamName}</h3>
                <p>{team.basepoin}</p>
              </Carousel.Caption>
            </Carousel.Item>
          ))}
        </Carousel>

        <div className="p-2 row gap-3">
          {courtData.map((court) => (
            <Cards court={court} key={court.id} />
          ))}
        </div>
      </div>
  </>
  )
}

export default Teamsview;
