import React, { useEffect, useState } from 'react'
import NavScrollExample from '../components/common/Navbar'
import { Carousel } from 'react-bootstrap';
import Axiosinstance from '../config/Axiosinstances';
import { useNavigate } from 'react-router-dom';
import './css/Home.css'; // Import the custom CSS file
import { BASE_URL } from '../constants/constants';
import GroupCard from '../components/common/groupcard'; // Make sure to import GroupCard

function Teamsview() {
    const [courtData, setCourtData] = useState([])
    const navigate= useNavigate()

    useEffect(() => {
        getAllteamsData()
    }, []);

    const getAllteamsData = () => {
        Axiosinstance.get('/users/getAllteamsData').then((response) => {
            setCourtData(response.data)
        }).catch(err => {
            if (err.response.data.message === 'unauthorized user') {
                localStorage.clear()
                navigate('/')
            }
        })
    }

    return (
        <>
            <NavScrollExample />
            <div className="container-fluid p-b ">
                <Carousel>
                    {courtData.map((team) => (
                        <Carousel.Item key={team.id}>
                            <img
                                className="d-block w-100"
                                src={`${BASE_URL}/courts/${team.teamPic}`}
                                alt={team.teamName}
                            />
                            <Carousel.Caption>
                                <h3>{team.teamName}</h3>
                                <p>{team.basepoint}</p>
                            </Carousel.Caption>
                        </Carousel.Item>
                    ))}
                </Carousel>
            </div>
            <div className="container-fluid mt-2 f">
                <div className="row row-cols-1 row-cols-md-5 mx-2 g-4">
                    <div className="p-2 row gap-3">
                        {courtData.map((team) => (
                            <GroupCard team={team} key={team.id} />
                        ))}
                    </div>
                </div>
            </div>
        </>
    )
}

export default Teamsview;
