 const handleGroupSelection = (group) => {
    // Logic to send court data to backend with selected group
    const dataToSend = {
      courtName: editedCourtData.courtName,
      location: editedCourtData.location,
      about: editedCourtData.about,
      image: editedCourtData.image,
      group: group // Assuming group is passed as a parameter
    };

    Axiosinstance.post('/users/groups', dataToSend)
      .then((res) => {
        toastSucess('Court data added to group successfully');
        // Redirect to "My Group" page or update group data display
      })
      .catch((err) => {
        toastError('Failed to add court data to group');
        console.error(err);
      });
  };
