import React, { useEffect, useState } from 'react';
import NavScrollExample from '../components/common/Navbar';
import Axiosinstance from '../config/Axiosinstances';
import Cards from '../components/common/Cards';
import { useNavigate } from 'react-router-dom';
import '.././pages/css/Home.css'; // Import your custom CSS file
import c1 from './pictures/c1.jpg'
import c2 from './pictures/c2.jpg'
import c3 from './pictures/c3.jpg'
import c4 from './pictures/c4.jpg'


function Home() {
  const [courtData, setCourtData] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    getAllCourtsData();
  }, []);

  const getAllCourtsData = () => {
    Axiosinstance.get('/users/getAllcourtsData')
      .then((response) => {
        setCourtData(response.data);
      })
      .catch((err) => {
        if (err.response.data.message === 'unauthorized user') {
          localStorage.clear();
          navigate('/');
        }
      });
  };

  return (
    <>
      <NavScrollExample />
      
      <div className="carousel-container">
        {/* Bootstrap Carousel */}
        <div id="carouselExampleControls" className="carousel slide" data-bs-ride="carousel">
          <div className="carousel-inner">
            {/* Add your carousel items here */}
            <div className="carousel-item active">
              <img src={c1}className="d-block w-100" alt="Carousel Image 1" />
            </div>
            <div className="carousel-item">
              <img src={c2} className="d-block w-100" alt="Carousel Image 2" />
            </div>
            <div className="carousel-item">
              <img src={c3} className="d-block w-100" alt="Carousel Image 3" />
            </div>
            <div className="carousel-item">
              <img src={c4} className="d-block w-100" alt="Carousel Image 4" />
            </div>
          </div>
          <button className="carousel-control-prev" type="button" data-bs-target="#carouselExampleControls" data-bs-slide="prev">
            <span className="carousel-control-prev-icon" aria-hidden="true"></span>
            <span className="visually-hidden">Previous</span>
          </button>
          <button className="carousel-control-next" type="button" data-bs-target="#carouselExampleControls" data-bs-slide="next">
            <span className="carousel-control-next-icon" aria-hidden="true"></span>
            <span className="visually-hidden">Next</span>
          </button>
        </div>
      </div>
      <div className="d-flex ">
        <marquee
          behaviour="scroll"
          direction="right"
          className="rolling booking  mr">
          <h4>"Elevate Your Squad: The Auction Awaits Your Mastery!"</h4>
        </marquee>
      </div>
      <div className="container-fluid mt-4 f">
        div.container
        <h3 className='text-light'>Players</h3>
        <div className="row row-cols-1 row-cols-md-3 g-4">
          {courtData.map((court) => (
            <div key={court._id} className="col">
              <Cards court={court} />
            </div>
          ))}
        </div>
      </div>
    </>
  );
}

export default Home;
