import React from 'react';
import {
  MDBCard,
  MDBCardBody,
  MDBCardTitle,
  MDBCardText,
  MDBCardImage,
  MDBBtn,
  MDBRipple
} from 'mdb-react-ui-kit';
import { BASE_URL } from '../../constants/constants';

export default function ButtonList({ group }) {
  return (
    <MDBCard>
      <MDBRipple rippleColor='light' rippleTag='div' className='bg-image hover-overlay'>
        <MDBCardImage src={`${BASE_URL}/courts/${group.courtData.courtPic}`} fluid alt='...' />
        <a>
          <div className='mask' style={{ backgroundColor: 'rgba(251, 251, 251, 0.15)' }}></div>
        </a>
      </MDBRipple>
      <MDBCardBody>
        <MDBCardTitle>{group.courtData.courtName}</MDBCardTitle>
        <MDBCardText>
          Time: {group.slot.name} {/* Assuming 'slot' contains the time */}
        </MDBCardText>
        {/* Depending on your data structure, you may or may not have 'day', 'month', 'year' separately */}
        {/* <MDBCardText>
          Date: {day}/{month}/{year}
        </MDBCardText> */}
        <MDBCardText>
          Location: {group.courtData.location}
        </MDBCardText>
        <MDBCardText>
          Address: {group.courtData.address}
        </MDBCardText>
      </MDBCardBody>
    </MDBCard>
  );
}
