import React from 'react';
import { MDBCard, MDBCardBody, MDBCardTitle, MDBCardSubTitle, MDBCardText, MDBCardImage, MDBBtn, MDBRipple } from 'mdb-react-ui-kit';
import { BASE_URL } from '../../constants/constants';
import { useNavigate } from 'react-router-dom';
import './cards.css'; // Import your custom CSS file

export default function Cards({court}) {
  const navigate = useNavigate();

  return (
    <MDBCard className='card d' onClick={() => navigate(`/courtUserViewPage/${court._id}`)}>
      <MDBRipple rippleColor='light' rippleTag='div' className='card-image image text-cent'>
        <MDBCardImage src={`${BASE_URL}/courts/${court.courtPic}`} fluid alt='...' />
      </MDBRipple>
      <MDBCardBody className='card-body'>
        <MDBCardTitle className='card-title title'>{court?.courtName}</MDBCardTitle>
        <MDBCardSubTitle className='card-subtitle subtitle'>{court?.type}</MDBCardSubTitle>
        <MDBCardSubTitle className='card-subtitle subtitle'>{court?.location}</MDBCardSubTitle>
        <MDBCardText className='card-text text'>{court.about}</MDBCardText>
        <MDBBtn  className="btn  text-light ms-2" style={{background: '  #010203'}}>Button</MDBBtn>
      </MDBCardBody>
    </MDBCard>
  );
}
