import React, { useState, useEffect } from 'react';
import NavScrollExample from './Navbar';
import Axiosinstance from '../../config/Axiosinstances';
import { BASE_URL } from '../../constants/constants';
import Table from 'react-bootstrap/Table';

function GroupPage() {
    const [groups, setGroups] = useState([]);

    useEffect(() => {
        fetchGroups();
    }, []);

    const fetchGroups = async () => {
        try {
            const response = await Axiosinstance.get('/users/getgroups');
            setGroups(response.data);
        } catch (error) {
            console.error('Error fetching groups:', error);
        }
    };

    return (
        <div>
            <NavScrollExample/>
            {groups.map(group => (
                <div key={group._id}>
                    <h2 className='text-center' >{group.groupName}</h2>
                    <Table striped bordered hover>
                        <thead>
                            <tr>
                                <th>Court Name</th>
                                <th>Price</th>
                                {/* Add additional table headers as needed */}
                            </tr>
                        </thead>
                        <tbody>
                            {group.courts.map(court => (
                                <tr key={court._id}>
                                    <td>{court.courtName}</td>
                                    <td>{court.price}</td>
                                    {/* Add additional table cells for player names and about */}
                                </tr>
                            ))}
                            <tr>
                                <td> </td>
                            </tr>
                        </tbody>
                    </Table>
                    <div className="d-flex justify-content-between mt-3">
                        <div>
                            <h5>Total Court Price:</h5>
                            <p>
                                {group.courts.reduce((acc, court) => acc + parseFloat(court.price), 0)}
                            </p>
                        </div>
                        <div>
                            <h5>Total Amount (Court Price - 100000):</h5>
                            <p>
                                {group.courts.reduce((acc, court) => acc + parseFloat(court.price), 0) - 100000}
                            </p>
                        </div>
                    </div>
                </div>
            ))}
        </div>
    );
}

export default GroupPage;
