import React, { useState, useEffect } from 'react';
import NavScrollExample from './Navbar';
import Axiosinstance from '../../config/Axiosinstances';
import { BASE_URL } from '../../constants/constants';
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';


function GroupPage() {
    const [groups, setGroups] = useState([]);

    useEffect(() => {
        fetchGroups();
    }, []);

    const fetchGroups = async () => {
        try {
            const response = await Axiosinstance.get('/users/getgroups'); // Assuming the endpoint to fetch groups
            setGroups(response.data);
            console.log(response.data);
        } catch (error) {
            console.error('Error fetching groups:', error);
        }
    };

    return (
        <div>
           <NavScrollExample/> 
            {groups.map(group => (
                <div key={group._id}>
                    <h2>{group.groupName}</h2>
                    <div>
                        {group.courts.map(court => (
                            <div  className="">
                           <Card key={court._id} style={{ width: '18rem' }}>
                          <Card.Img variant="top" src={`${BASE_URL}/courts/${court.image}`} alt="court "/>
                          <Card.Body>
                            <Card.Title>{court.courtName}</Card.Title>
                            <Card.Text>
                            {court.location}
                               </Card.Text>
                            <Button variant="primary">Go somewhere</Button>
                          </Card.Body>
                        </Card>
                              
                           </div>
                        ))}
                    </div>
                </div>
            ))}
        </div>
    );
}

export default GroupPage;
