import React from 'react';

function MyPlayer({ location }) {
  // Existing code...

  // Check if location and location.state exist
  if (!location || !location.state || !location.state.group) {
    // Handle the case where location, state, or group is undefined
    return <div>No data available</div>;
  }

  const { state } = location;

  // Filter players based on the selected group
  const filteredPlayers = state.group
    ? players.filter((player) => player.group === state.group)
    : players;

  return (
    <div>
      <h1>My Players Page - Group {state.group}</h1>
      <ul>
        {filteredPlayers.map((player, index) => (
          <li key={index}>
            {/* Display player information */}
            <div>Name: {player.name}</div>
            <div>Group: {player.group}</div>
            {/* Add more player details as needed */}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default MyPlayer;
