import React, { useState, useEffect } from 'react';
import NavScrollExample from './Navbar';
import Axiosinstance from '../../config/Axiosinstances';
import { BASE_URL } from '../../constants/constants';
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';

function GroupPage() {
    const [groups, setGroups] = useState([]);

    useEffect(() => {
        fetchGroups();
    }, []);

    const fetchGroups = async () => {
        try {
            const response = await Axiosinstance.get('/users/getgroups');
            setGroups(response.data);
        } catch (error) {
            console.error('Error fetching groups:', error);
        }
    };

    return (
        <div>
            <NavScrollExample/>
            {groups.map(group => (
                <div key={group._id}>
                    <h2>{group.groupName}</h2>
                    <div className="d-flex ">
                        {group.courts.map(court => (
                            <div className="row mx-2" key={court._id}>
                                <Card style={{ width: '14rem' }}>
                                    <Card.Img variant="top" src={`${BASE_URL}/courts/${court.image}`} alt="court"/>
                                    <Card.Body>
                                        <Card.Title>{court.courtName}</Card.Title>
                                        <Card.Text>
                                            {court.price}
                                        </Card.Text>
                                        <Button variant="primary">Go somewhere</Button>
                                    </Card.Body>
                                </Card>
                            </div>
                        ))}
                    </div>
                    <div className="d-flex justify-content-between mt-3">
                        <div>
                            <h5>Total Court Price:</h5>
                            <p>
                                {group.courts.reduce((acc, court) => acc + court.price, 0)}
                            </p>
                        </div>
                        <div>
                            <h5>Total Amount (Court Price - 100000):</h5>
                            <p>
                                {group.courts.reduce((acc, court) => acc + court.price, 0) - 100000}
                            </p>
                        </div>
                    </div>
                </div>
            ))}
        </div>
    );
}

export default GroupPage;
