import React, { useEffect, useState } from 'react';
import Axiosinstance from '../../config/Axiosinstances';
import { useParams } from 'react-router-dom';
import NavScrollExample from './Navbar';
import Card from 'react-bootstrap/Card';
import { BASE_URL } from '../../constants/constants';

function GroupDetailPage() {
    const { groupId } = useParams();
    const [group, setGroup] = useState(null);

    useEffect(() => {
        fetchGroupDetails();
    }, []);

    const fetchGroupDetails = () => {
        Axiosinstance.get(`/users/groups/${groupId}`)
            .then(response => {
                setGroup(response.data);
            })
            .catch(error => {
                console.error('Error fetching group details:', error);
            });
    };

    if (!group) {
        return <div>Loading...</div>;
    }

    return (
        <div>
            <NavScrollExample />
            <h1>Group Details</h1>
            <h2>Group Name: {group.groupName}</h2>
            <div>
                {group.courts.map(court => (
                    <div key={court._id}>
                        <h3>Court Name: {court.courtName}</h3>
                        <p>Location: {court.location}</p>
                        <p>About: {court.about}</p>
                        <img src={`${BASE_URL}/courts/${court.image}`} alt={court.courtName} />
                        {/* Add any other details you want to display */}
                    </div>
                ))}
            </div>
        </div>
    );
}

export default GroupDetailPage;
