import React, { useState, useEffect } from 'react';
import NavScrollExample from './Navbar';
import Axiosinstance from '../../config/Axiosinstances';
import { BASE_URL } from '../../constants/constants';
import Table from 'react-bootstrap/Table';
import './CoutBooking.css';
import { toastSucess } from '../../constants/Plugin';


function GroupPage() {
    const [groups, setGroups] = useState([]);

    useEffect(() => {
        fetchGroups();
    }, []);

    const fetchGroups = async () => {
        try {
            const response = await Axiosinstance.get('/users/getgroups');
            setGroups(response.data);
        } catch (error) {
            console.error('Error fetching groups:', error);
        }
    };

    const handleDeleteCourt = async (groupId, courtId) => {
        try {
            // Send a request to delete the court with the specified ID
            await Axiosinstance.delete(`/users/deletegroup/${groupId}/${courtId}`);
            // After successful deletion, fetch groups again to update the UI
            fetchGroups();
            toastSucess("deleted suc")
        } catch (error) {
            console.error('Error deleting court:', error);
        }
    };

    return (
        <div>
            <NavScrollExample/>
            {groups.map(group => (
                <div key={group._id}>
                    <h2 className='text-center' style={{fontFamily:'sans-serif'}} >{group.groupName}</h2>
                     <h4>TOTAL POINTS: 100000</h4>
                    <Table striped bordered hover>
                        <thead>
                            <tr>
                                <th>PLAYER NAME</th>
                                <th>POINTS</th>
                                {/* <th>Action</th>  */}
                            </tr>
                        </thead>
                        <tbody>
                            {group.courts.map(court => (
                                <tr key={court._id}>
                                    <td>{court.courtName}  <button className='mx-5 ' onClick={() => handleDeleteCourt(group._id, court._id)}>Delete</button></td>
                                    <td>{court.price}</td>
                                    {/* <td>
                                       
                                        <button onClick={() => handleDeleteCourt(group._id, court._id)}>Delete</button>
                                    </td> */}
                                </tr>
                            ))}
                            <tr>
                                <td>TOTAL POINTS</td>
                                <td> {group.courts.reduce((acc, court) => acc + parseFloat(court.price), 0)}</td>
                                <td></td> {/* Empty cell for alignment */}
                            </tr>
                            <tr>
                                <td>BALANCE</td>
                                <td>{group.courts.reduce((acc, court) => acc + parseFloat(court.price), 0) - 100000}</td>
                                <td></td> {/* Empty cell for alignment */}
                            </tr>
                        </tbody>
                    </Table>
                </div>
            ))}
        </div>
    );
}

export default GroupPage;
